
$('.toggle-class').change(function () {
    var status = $(this).prop('checked') == true ? 1 : 0;
    var item_id = $(this).data('id');
    $.ajax({
        type: 'GET',
        dataType: 'json',
        url: '/test',
        data: {
            'status': status,
            'item_id': item_id
        },
        success: function (data) {
            console.log(data.success)
        }
    });
});
$('.toggle-delay').change(function () {
    var status = $(this).prop('checked') == true ? 1 : 0;
    var item_id = $(this).data('id');
    $.ajax({
        type: 'GET',
        dataType: 'json',
        url: '/ChangeSttDelay',
        data: {
            'status': status,
            'item_id': item_id
        },
        success: function (data) {
            $('#notifDiv').fadeIn();
            $('#notifDiv').css('background', 'green');
            $('#notifDiv').text('Updated Successfully');
            setTimeout(() => {
                $('#notifDiv').fadeOut();
            }, 1500);
        }
    });
});
